CREATE TABLE `anime` (
  `no` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `judul_anime` varchar(500) CHARACTER SET latin1 NOT NULL,
  `english` varchar(300) CHARACTER SET latin1 NOT NULL,
  `japanese` varchar(300) COLLATE utf32_bin NOT NULL,
  `synonim` varchar(300) CHARACTER SET latin1 NOT NULL,
  `type` varchar(300) CHARACTER SET latin1 NOT NULL,
  `episode` varchar(300) COLLATE utf32_bin NOT NULL,
  `status` varchar(300) CHARACTER SET latin1 NOT NULL,
  `musim` varchar(300) CHARACTER SET latin1 NOT NULL,
  `sumber` varchar(300) COLLATE utf32_bin NOT NULL,
  `durasi` varchar(300) COLLATE utf32_bin NOT NULL,
  `rating` varchar(300) COLLATE utf32_bin NOT NULL,
  `synopsis` varchar(10000) COLLATE utf32_bin NOT NULL,
  `gambar` varchar(500) COLLATE utf32_bin NOT NULL,
  `triller` varchar(500) COLLATE utf32_bin NOT NULL,
  `waktu_upload` varchar(500) COLLATE utf32_bin NOT NULL,
  `rekomendasi` varchar(100) COLLATE utf32_bin NOT NULL,
  `tahun` int(10) unsigned NOT NULL,
  `studio` varchar(300) CHARACTER SET latin1 COLLATE latin1_bin NOT NULL,
  `lisensi` varchar(300) CHARACTER SET latin1 COLLATE latin1_bin NOT NULL,
  `official` varchar(500) COLLATE utf32_bin NOT NULL,
  PRIMARY KEY (`no`),
  UNIQUE KEY `judul_anime_2` (`judul_anime`),
  KEY `judul_anime` (`judul_anime`(250)),
  KEY `judul_anime_3` (`judul_anime`),
  KEY `type` (`type`),
  KEY `musim` (`musim`),
  KEY `status` (`status`)
) ENGINE=MyISAM AUTO_INCREMENT=28 DEFAULT CHARSET=utf32 COLLATE=utf32_bin;

