  `no` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `judul_artikel` varchar(300) CHARACTER SET utf32 COLLATE utf32_unicode_ci NOT NULL,
  `isi` mediumtext CHARACTER SET utf32 COLLATE utf32_unicode_ci NOT NULL,
  `gambar` varchar(500) COLLATE utf32_bin NOT NULL,
  `waktu_artikel` datetime NOT NULL DEFAULT current_timestamp(),
  `status` int(11) NOT NULL,
  `view` int(11) NOT NULL,
  `oleh` varchar(300) CHARACTER SET utf32 COLLATE utf32_unicode_ci NOT NULL,
  `kategori` varchar(300) CHARACTER SET utf32 COLLATE utf32_unicode_ci NOT NULL,
  `tag` varchar(500) COLLATE utf32_bin NOT NULL,
  PRIMARY KEY (`no`),
  KEY `judul_artikel` (`judul_artikel`(250))
) ENGINE=MyISAM AUTO_INCREMENT=253 DEFAULT CHARSET=utf32 COLLATE=utf32_bin;

