INSERT INTO artikel VALUES('69','Lagu OP Kimetsu no Yaiba- LiSA Mendominasi Chart Digital Mingguan Terakhir di Heiesi Era','&lt;img src=&quot;https://img1.ak.crunchyroll.com/i/spire3/4be3c09118be57efdd6191efd9d9ea371556589419_full.jpg&quot; style=&quot;width: 300px; height: 300px;&quot;&gt;
