INSERT INTO artikel VALUES('88','Live-Action : Machida-kuntd_ptks world (Machida-kun no Sekai)','&lt;p&gt;&lt;span style=&quot;font-family: td_ptktimes new romantd_ptk, times, serif;&quot;&gt;Satu lagi rekomendasi film untuk tahun 2019, Machida-kuntd_ptks world sebuat film adaptasi dari manga Machida-kuntd_ptks world (Machida-kun no &lt;/span&gt;&lt;span style=&quot;font-family: td_ptktimes new romantd_ptk, times, serif;&quot;&gt;Sekai) karya Yuki Ando.&lt;img style=&quot;float: right;&quot; src=&quot;https://cdnx.natalie.mu/media/news/comic/2019/0505/machida_pos_fixw_234.jpg&quot; /&gt; &lt;/span&gt;&lt;/p&gt;
&lt;p&gt;&lt;span style=&quot;font-family: td_ptktimes new romantd_ptk, times, serif;&quot;&gt;Manga yang dirilis dari tahun 2013 sampai tahun 2018 ini telah banyak memenangkan penghargaan seperti p&lt;span lang=&quot;id&quot; tabindex=&quot;0&quot;&gt;ada 2015, Machida-kun&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;&lt;span lang=&quot;id&quot; style=&quot;font-family: td_ptktimes new romantd_ptk, times, serif;&quot; tabindex=&quot;0&quot;&gt;no Sekai memenangkan Penghargaan Wajah Baru Festival Media Jepang ke-19; dan pada tahun 2016, dianugerahi Penghargaan Pencipta Baru Tezuka Osamu ke-20.&lt;/span&gt;&lt;/p&gt;
